		tax,
		discount,
		item_discount,
		subtotal,
		serial_no,
		real_unit_price,
		product_noted
	) SELECT
		id,
		sale_id,
		product_id,
		product_code,
		product_name,
		product_type,
		option_id,
		net_unit_price,
		unit_price,
		quantity,
		warehouse_id,
		item_tax,
		tax_rate_id,
		tax,
		discount,
		item_discount,
		subtotal,
		serial_no,
		real_unit_price,
